			const bits = id.split("qfrbtn");
			const cbsrl = bits[1];
			const mechlist = document.getElementById('mechanic');
			const notes = document.getElementsByName('rnotes'+cbsrl)[0].value;

			if (mechlist.selectedIndex < 2) {
				alert('Please select a mechanic');
				return;
			} 
			else 
